package operators;

public class BitWise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4,b=6;
		System.out.println(" a & b =" + (a&b));
		System.out.println(" a | b = " +(a|b));
		System.out.println(" a ^ b = " +(a^b));
		System.out.println("~ a = " +(~a));
		System.out.println(" a >> b = " +(a>>b));
		System.out.println(" a << b = " +(a<<b));
	}

}
